
public class OperatorExam5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//삼항 연산자
		int b1 =( 5 < 4 ) ? 50: 40;
		//       ( 조건 )  ? true:false
		System.out.println(b1);
		
		int b2 = 0;
		if(5 < 4) {
			b2 = 50;
			
		}else {
			b2 =40;
		}
		System.out.println(b2);

	}

}
